let archiv (jahr: Nat) (woche: Nat) (wochentag: Wochentag): List<Ausgabe * Essen> =
  match Map.tryFind jahr data with
  | None -> []
  | Some jahrData -> match Map.tryFind woche jahrData with
                     | None -> []
                     | Some wochenData -> match Map.tryFind wochentag wochenData with
                                          | None -> []
                                          | Some result -> result
